import SaveAction from "@enhavo/app/Action/Model/SaveAction";
import AbstractFactory from "@enhavo/app/Action/Factory/AbstractFactory";
import View from "@enhavo/app/View/View";
import EventDispatcher from "@enhavo/app/ViewStack/EventDispatcher";
import Router from "@enhavo/core/Router";

export default class SaveActionFactory extends AbstractFactory
{
    private readonly view: View;
    private readonly eventDispatcher: EventDispatcher;
    private readonly router: Router;

    constructor(view: View, eventDispatcher: EventDispatcher, router: Router) {
        super();
        this.view = view;
        this.eventDispatcher = eventDispatcher;
        this.router = router;
    }

    createNew(): SaveAction {
        return new SaveAction(this.view, this.eventDispatcher, this.router);
    }
}
