import FormType from "@enhavo/app/Form/FormType";
import 'jquery-datetimepicker'
import 'jquery-datetimepicker/build/jquery.datetimepicker.min.css'

export default class DateTimeType extends FormType
{
    protected init()
    {
        let options = this.$element.data('date-time-picker');
        if (options === '') {
            options = {};
        }
        if (!options.hasOwnProperty('format')) { options.format = 'd.m.Y H:i'; }
        if (!options.hasOwnProperty('dayOfWeekStart')) { options.dayOfWeekStart = 1; }
        if (!options.hasOwnProperty('scrollInput')) { options.scrollInput = false; }
        $.datetimepicker.setLocale('de');
        this.$element.datetimepicker(options).attr('readonly', true);
    }
}