<template>
    <input type="hidden"
           :name="form.fullName"
           :required="form.required"
           :disabled="form.disabled"
           v-model="form.value"
           ref="element"
           @change="form.dispatchChange()"
    />
    <div class="wysiwyg-container">
        <div :id="form.editorId" ref="editorElement" v-once></div>
    </div>
</template>

<script setup lang="ts">
import {onMounted, onUpdated, ref} from "vue";
import {FormUtil} from "@enhavo/vue-form/form/FormUtil";
import {WysiwygForm} from "@enhavo/form/form/model/WysiwygForm";

const props = defineProps<{
    form: WysiwygForm
}>()

const form = props.form;

onUpdated(() => {
    const element = ref<HTMLElement>(null).value;
    form.element = element;
    FormUtil.updateAttributes(element, form.attr);

    if (form.editorElement.id !== form.editorId) {
        form.initWysiwyg();
    }
});

onMounted(() => {
    const element = ref<HTMLElement>(null).value;
    form.element = element;
    FormUtil.updateAttributes(element, form.attr);

    form.initWysiwyg();
});
</script>
