import FormType from "@enhavo/app/form/FormType";
import 'jquery-datetimepicker'
import 'jquery-datetimepicker/build/jquery.datetimepicker.min.css'
import * as jQuery from "jquery";

export default class DateTimeType extends FormType
{
    protected init()
    {
        let options = {
            'format': 'd.m.Y H:i',
            'dayOfWeekStart': 1,
            'scrollInput': false
        };

        let configType = this.$element.data('date-time-picker');
        jQuery(document).trigger('datePickerInit', {
            configType: configType,
            options: options
        });

        jQuery.datetimepicker.setLocale('de');
        this.$element.datetimepicker(options);

        this.$element.parent().find('[data-clear-date-button]').on('click', () => {
            this.$element.val('');
        });
    }
}