import axios, {CancelTokenSource} from "axios";
import * as _ from "lodash";
import Data, {ContentType, Item, Tag} from "@enhavo/media-library/Data";
import UpdatedEvent from "@enhavo/app/view-stack/event/UpdatedEvent";
import * as async from "async";
import Message from "@enhavo/app/flash-message/Message";
import FlashMessenger from "@enhavo/app/flash-message/FlashMessenger";
import EventDispatcher from "@enhavo/app/view-stack/EventDispatcher";
import View from "@enhavo/app/view/View";
import Router from "@enhavo/core/Router";
import ComponentRegistryInterface from "@enhavo/core/ComponentRegistryInterface";
import ViewInterface from "@enhavo/app/view-stack/ViewInterface";
import Translator from "@enhavo/core/Translator";

export default class MediaLibrary
{
    public data: Data;
    private source: CancelTokenSource;
    private readonly eventDispatcher: EventDispatcher;
    private readonly flashMessenger: FlashMessenger;
    private readonly view: View;
    private readonly router: Router;
    private readonly componentRegistry: ComponentRegistryInterface;
    private readonly translator: Translator;

    public constructor(data: any, eventDispatcher: EventDispatcher, view: View, flashMessenger: FlashMessenger, router: Router, componentRegistry: ComponentRegistryInterface, translator: Translator)
    {
        this.data = _.extend(data.data, new Data());
        this.flashMessenger = flashMessenger;
        this.eventDispatcher = eventDispatcher;
        this.view = view;
        this.router = router;
        this.componentRegistry = componentRegistry;
        this.translator = translator;

        $(document).on('add', () => {
            this.add();
        });
    }

    public init()
    {
        this.componentRegistry.registerStore('mediaLibrary', this);
        this.data = this.componentRegistry.registerData(this.data);

        this.getTags();
        this.getContentTypes();
        this.getFiles();
    }

    private add()
    {
        this.view.loading();
        let calls: ((callback: (err: any) => void) => void)[] = [];
        for(let image of this.data.selectedItems) {
            calls.push((callback) => {
                axios
                    .get(this.router.generate('enhavo_media_library_file_add', {id: image.id}))
                    .then(response => {
                        this.eventDispatcher.dispatch(new UpdatedEvent(this.view.getId(), response.data));
                        callback(null);
                    })
                    .catch(error => {
                        callback('error');
                    })
            });
        }

        async.parallel(calls,(err) => {
            if(err) {
                this.flashMessenger.addMessage(new Message(Message.ERROR, 'media.library.add.error'));
            } else {
                this.flashMessenger.addMessage(new Message(Message.SUCCESS, 'media.library.add.success'));
            }
            this.view.loaded();
            this.data.selectedItems = [];
        });
    }

    public loading() {
        this.view.loading();
        this.data.loading = true;
    }

    public loaded() {
        this.view.loaded();
        this.data.loading = false;
    }

    public setProgress(value: number)
    {
        this.data.progress = value;
    }

    public open(item: Item): void
    {
        if (this.data.mode === 'edit') {
            this.activateItem(item).then(() => {
                let url = this.router.generate('enhavo_media_library_file_update', {id: item.id, view_id: this.view.getId()});
                this.view.open(url, 'edit-view').then((view: ViewInterface) => {
                    this.view.storeValue('active-view', view.id);
                });
            });

        } else if (this.data.mode === 'select') {
            if (!this.isItemSelected(item)) {
                if (this.data.multiple) {
                    this.addItem(item);
                } else {
                    this.data.selectedItems = [];
                    this.addItem(item);
                }
            } else {
                this.removeItem(item);
            }

            this.checkItemSelection();
        }
    }

    private activateItem(item: Item)
    {
        return new Promise((resolve, reject) => {
            async.parallel([(callback: (err: any) => void) => {
                this.view.storeValue('active-view', null).then(() => {
                    callback(null);
                }).catch(() => {
                    callback(true);
                });
            }], (err) => {
                if(err) {
                    reject();
                } else {
                    resolve();
                }
            });
        });
    }

    private checkItemSelection()
    {
        for(let item of this.data.items) {
            item.selected = this.isItemSelected(item);
        }
    }

    public isItemSelected(item: Item): boolean
    {
        for(let selectedItem of this.data.selectedItems) {
            if(selectedItem.id == item.id) {
                return true;
            }
        }
        return false;
    }

    private removeItem(item: Item)
    {
        let index: number = -1;
        for(let i in this.data.selectedItems) {
            if(this.data.selectedItems[i].id == item.id) {
                index = parseInt(i);
                break;
            }
        }

        if (index !== -1) {
            this.data.selectedItems.splice(index, 1);
        }
    }

    private addItem(item: any)
    {
        this.data.selectedItems.push(item);
    }

    public setActiveTag(id:any)
    {
        if (this.data.activeTag === id) {
            this.data.activeTag = null;
        }
        else {
            this.data.activeTag = id;
        }
        this.getFiles();
    }

    public setActiveContentType(key:any)
    {
        if (this.data.activeContentType === key) {
            this.data.activeContentType = null;
        } else {
            this.data.activeContentType = key;
        }
        this.getFiles();
    }

    public update()
    {
        this.getFiles();
    }

    public fail(message: string)
    {
        this.flashMessenger.addMessage(new Message(Message.ERROR, message));
    }

    public refresh()
    {
        this.getFiles();
    }

    private getFiles()
    {
        this.loading();
        let url = this.router.generate('enhavo_media_library_file_files');

        if(this.source != null) {
            this.source.cancel();
        }
        this.source = axios.CancelToken.source();
        axios
            .get(url, {
                params: {
                    content_type: this.data.activeContentType,
                    tag: this.data.activeTag,
                    search: this.data.searchString,
                },
                cancelToken: this.source.token
            })
            .then(response => {
                this.source = null;

                let items = [];
                let itemsData = response.data.items;
                for(let itemData of itemsData) {
                    let item = new Item();
                    item.id = itemData.id;
                    item.previewImageUrl = itemData.previewImageUrl;
                    item.label = itemData.label;
                    item.selected = false;
                    items.push(item);
                }
                this.data.items = items;
                this.checkItemSelection();
                this.loaded();
            });
    }


    private getTags()
    {
        this.loading();
        let url = this.router.generate('enhavo_media_library_file_tags');

        axios
            .get(url, {
                params: {
                    contentType: this.data.activeContentType,
                    tag: this.data.activeTag,
                    search: this.data.searchString,
                },
            })
            .then(response => {
                let tags = [];
                let tagsData = response.data.tags;
                for(let tagData of tagsData) {
                    let item = new Tag();
                    item.id = tagData.id;
                    item.label = tagData.label;
                    tags.push(item);
                }
                this.data.tags = tags;
                this.loaded();
            });
    }

    private getContentTypes()
    {
        this.loading();
        let url = this.router.generate('enhavo_media_library_file_content_types');

        axios
            .get(url, {
                params: {
                    contentType: this.data.activeContentType,
                    tag: this.data.activeTag,
                    search: this.data.searchString,
                },
            })
            .then(response => {
                let contentTypes = [];
                let contentTypesData = response.data.content_types;
                for(let contentTypeData of contentTypesData) {
                    let item = new ContentType();
                    item.key = contentTypeData.key;
                    item.label = contentTypeData.label;
                    contentTypes.push(item);
                }
                this.data.contentTypes = contentTypes;
                this.loaded();
            });
    }

    public search()
    {
        this.getFiles();
    }

    public clearSearch()
    {
        this.data.searchString = '';
        this.getFiles();
    }


    public showDropZone() {
        if(!this.data.dropZone) {
            this.data.dropZone = true;
        }
    }

    public showDropZoneActive() {
        if(!this.data.dropZoneActive) {
            this.data.dropZoneActive = true;
        }
    }

    public hideDropZone() {
        if(this.data.dropZone) {
            this.data.dropZone = false;
        }
    }

    public hideDropZoneActive() {
        if(this.data.dropZoneActive) {
            this.data.dropZoneActive = false;
        }
    }
}
