<template>
    <li>
        <div class="tag-name" v-bind:contentType="contentType" @click="onClick()">{{ contentType.label }}</div>
    </li>
</template>

<script lang="ts">
import {Inject, Options, Prop, Vue} from "vue-property-decorator";
import {ContentType} from "../Data";
import MediaLibraryApp from "@enhavo/media-library/MediaLibraryApp";

@Options({})
export default class extends Vue {
    @Prop()
    contentType: ContentType;

    @Inject()
    mediaLibrary: MediaLibraryApp;

    onClick() {
        this.mediaLibrary.setActiveContentType(this.contentType.key);
    }

}
</script>
