<template>
    <li>
        <div @click="onClick()" v-bind:contentType="contentType" class="tag-name">{{ contentType.label }}</div>
    </li>
</template>

<script lang="ts">
    import { Vue, Component, Prop } from "vue-property-decorator";
    import {ContentType} from "../Data";

    @Component({})
    export default class ContentTypeComponent extends Vue
    {
        name: 'content-type-component';

        @Prop()
        contentType: ContentType;

        onClick()
        {
            this.$MediaLibrary.setActiveContentType(this.contentType.key);
        }

        mounted()
        {
        }
    }
</script>
