<template>
    <li @click="onClick()" :class="{'active': item.selected}">
        <span v-if="item.selected"><i class="icon icon-check"></i></span>
        <div class="img" :style="{ backgroundImage: 'url(' + item.previewImageUrl + ')' }"></div>
        <div class="title">{{ item.name }}</div>
    </li>
</template>

<script lang="ts">
    import { Vue, Component, Prop } from "vue-property-decorator";
    import {Item} from "@enhavo/media-library/Data";

    @Component({})
    export default class ImageComponent extends Vue
    {
        name: 'tag-component';

        @Prop()
        item: Item;

        onClick()
        {
          this.$MediaLibrary.toggleItem(this.item);
        }
    }
</script>
