<template>
    <li :class="{'active': item.selected}" @click="onClick()">
        <span v-if="item.selected"><i class="icon icon-check"></i></span>
        <div :style="{ backgroundImage: 'url(' + item.previewImageUrl + ')' }" class="img thumb">
            <span><i class="icon" :class="item.icon"></i></span>
        </div>
        <div class="title">{{ item.label }}</div>
    </li>
</template>

<script lang="ts">
import {Inject, Options, Prop, Vue} from "vue-property-decorator";
import {Item} from "@enhavo/media-library/Data";
import MediaLibrary from "@enhavo/media-library/MediaLibrary";

@Options({})
export default class extends Vue {
    @Inject()
    mediaLibrary: MediaLibrary;

    @Prop()
    item: Item;

    onClick() {
        this.item.selected = !this.item.selected;
        this.mediaLibrary.open(this.item);
    }
}
</script>
