<template>
    <li :class="{'active': item.selected}" @click="onClick()">
        <span v-if="item.selected"><i class="icon icon-check"></i></span>
        <div :style="{ backgroundImage: 'url(' + item.previewImageUrl + ')' }" class="img"></div>
        <div class="title">{{ item.name }}</div>
    </li>
</template>

<script lang="ts">
import {Inject, Options, Prop, Vue} from "vue-property-decorator";
import {Item} from "@enhavo/media-library/Data";
import MediaLibraryApp from "@enhavo/media-library/MediaLibraryApp";

@Options({})
export default class extends Vue {
    @Inject()
    mediaLibrary: MediaLibraryApp;

    @Prop()
    item: Item;

    onClick() {
        this.mediaLibrary.open(this.item);
    }
}
</script>
