<template>
        <table>
            <tr>
                <th v-for="column in mediaLibrary.data.columns" @click="onSort(column)">
                    {{ column.label }}
                </th>
            </tr>
            <file-list
                v-for="file of mediaLibrary.data.files"
                :file="file"
            ></file-list>
        </table>
</template>

<script lang="ts">
import {Inject, Options, Vue, Watch} from "vue-property-decorator";
import '@enhavo/app/assets/styles/view.scss';
import MediaLibrary from "@enhavo/media-library/MediaLibrary";
import FileThumbnail from "@enhavo/media-library/components/FileThumbnail.vue";
import {Column} from "@enhavo/media-library/Data";

@Options({
    components: {FileThumbnail}
})
export default class extends Vue {

    @Inject()
    mediaLibrary: MediaLibrary;

    public onSort(column: Column) {
        this.mediaLibrary.setSortColumn(column);
    }

}
</script>
