<template>
    <div class="tab-head" @click="$emit('change')" :class="{'selected': selected}">
        {{ tab.label }}
    </div>
</template>

<script lang="ts">
    import { Vue, Component, Prop } from "vue-property-decorator";
    import Tab from "@enhavo/app/form/Tab";

    @Component({})
    export default class TabHead extends Vue {
        name = 'tab-head';

        @Prop()
        tab: Tab;

        @Prop()
        selected: boolean;
    }
</script>


