<template>
    <li>
        <div class="tag-name" v-bind:tag="tag" @click="onClick()">{{ tag.label }}</div>
    </li>
</template>

<script lang="ts">
import {Inject, Options, Prop, Vue} from "vue-property-decorator";
import {Tag} from "../Data";
import MediaLibraryApp from "@enhavo/media-library/MediaLibraryApp";

@Options({})
export default class extends Vue {
    @Prop()
    tag: Tag;

    @Inject()
    mediaLibrary: MediaLibraryApp;

    onClick() {
        this.mediaLibrary.setActiveTag(this.tag.id);
    }

}
</script>
