<template>
    <li>
        <div @click="onClick()" v-bind:tag="tag" class="tag-name">{{ tag.label }}</div>
    </li>
</template>

<script lang="ts">
    import { Vue, Component, Prop } from "vue-property-decorator";
    import {Tag} from "../Data";

    @Component({})
    export default class TagComponent extends Vue
    {
        name: 'tag-component';

        @Prop()
        tag: Tag;

        onClick()
        {
            this.$MediaLibrary.setActiveTag(this.tag.id);
        }

        mounted()
        {
        }
    }
</script>
