import AbstractFactory from "@enhavo/app/Action/Factory/AbstractFactory";
import NewsletterSendTestAction from "@enhavo/newsletter/Action/Model/NewsletterSendTestAction";
import FlashMessenger from "@enhavo/app/FlashMessage/FlashMessenger";
import ModalManager from "@enhavo/app/Modal/ModalManager";
import Translator from "@enhavo/core/Translator";

export default class NewsletterSendTestActionFactory extends AbstractFactory
{
    protected readonly flashMessenger: FlashMessenger;
    protected readonly modalManager: ModalManager;
    protected readonly translator: Translator;

    public constructor(flashMessenger: FlashMessenger, modalManager: ModalManager, translator: Translator) {
        super();
        this.flashMessenger = flashMessenger;
        this.modalManager = modalManager;
    }

    createNew(): NewsletterSendTestAction {
        return new NewsletterSendTestAction(this.flashMessenger, this.modalManager, this.translator);
    }
}
