import {EventInterface} from '@enhavo/vue-form/event/EventInterface';
import {Listener} from "@enhavo/vue-form/form/Listener";

export interface FormEventDispatcher
{
    dispatchEvent(event: EventInterface, eventName: string);
    addListener(eventName: string | string[], callback: (event: EventInterface) => void);
    removeListener(listener: Listener);
}
